export const searchCourts = (courts, query) => {
  if (!query.trim()) return courts;
  
  const lowercaseQuery = query.toLowerCase();
  return courts.filter(court => 
    court.name.toLowerCase().includes(lowercaseQuery) ||
    court.location.toLowerCase().includes(lowercaseQuery) ||
    court.surface.toLowerCase().includes(lowercaseQuery) ||
    court.amenities.some(amenity => 
      amenity.toLowerCase().includes(lowercaseQuery)
    )
  );
};

export const sortCourts = (courts, sortBy) => {
  return [...courts].sort((a, b) => {
    switch (sortBy) {
      case 'rating':
        return b.rating - a.rating;
      case 'price':
        const priceA = parseFloat(a.price.replace(/[^\d.]/g, ''));
        const priceB = parseFloat(b.price.replace(/[^\d.]/g, ''));
        return priceA - priceB;
      case 'name':
        return a.name.localeCompare(b.name);
      case 'location':
        return a.location.localeCompare(b.location);
      default:
        return 0;
    }
  });
};