// Helper function to search courts
export const searchCourts = (courts, query) => {
  if (!query) return courts;
  
  const lowercaseQuery = query.toLowerCase();
  return courts.filter(court => 
    court.name.toLowerCase().includes(lowercaseQuery) ||
    court.location.toLowerCase().includes(lowercaseQuery) ||
    court.address.toLowerCase().includes(lowercaseQuery) ||
    court.surface.toLowerCase().includes(lowercaseQuery)
  );
};

// Helper function to sort courts
export const sortCourts = (courts, sortBy) => {
  switch (sortBy) {
    case 'rating':
      return [...courts].sort((a, b) => b.rating - a.rating);
    case 'price':
      return [...courts].sort((a, b) => {
        const priceA = parseInt(a.price.replace('$', '').replace('/hour', ''));
        const priceB = parseInt(b.price.replace('$', '').replace('/hour', ''));
        return priceA - priceB;
      });
    case 'name':
      return [...courts].sort((a, b) => a.name.localeCompare(b.name));
    default:
      return courts;
  }
};